<?php

// Idolon Class
require_once '../../Idolon.php';

// a complete
// config for Idolon
$aConfig = array(
    
    // path to directory where image resides
    'sImagePath' => realpath(__DIR__ . '/../'),
    
    // where to get string Image from
    'sImage' => (isset($_GET['i'])) ? $_GET['i'] : '',  // image name
    
    // where to get integer X Dimension from
    'iX' => (isset($_GET['x'])) ? $_GET['x'] : 0,   // x dimension
    
    // where to get integer Y Dimension from
    'iY' => (isset($_GET['y'])) ? $_GET['y'] : 0,   // y dimension
    
    // where to get integer R Ratio safe setting from
    'iRedirect' => (isset($_GET['r'])) ? $_GET['r'] : 1,   // ratio safe
    
    // Sanitize
    // cast all values to integer
    'oSanitize' => function(){
        (isset($_GET['x'])) ? $_GET['x'] = (int) $_GET['x'] : false; 
        (isset($_GET['y'])) ? $_GET['y'] = (int) $_GET['y'] : false;
        (isset($_GET['r'])) ? $_GET['r'] = (int) $_GET['r'] : $_GET['r'] = 1;            
    },
        
    // Filter
    // allow defined characters only
    'oFilter' => function(){
        (isset($_GET['i'])) ? $_GET['i'] = preg_replace('/[^a-zA-Z0-9\-\._, ]+/', '-', $_GET['i']) : false;
    },
        
    // path to imagemagick's convert binary
    'sConvertExecutable' => '/usr/bin/convert',
        
    // 404: a base64 image string
    's404Base64Image' => ''
);

$oIdolon = new \Idolon($aConfig);
$oIdolon->serve();

// get Log Messages from Idolon 
// and write into a file
file_put_contents(
    __DIR__ . '/default.log', 
    $oIdolon->getLog(), 
    FILE_APPEND
);     

